import * as React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useFonts, Kanit_200ExtraLight, Kanit_400Regular, Kanit_700Bold} from '@expo-google-fonts/kanit';
import { TextInput } from 'react-native-web';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Ionicons from '@expo/vector-icons/Ionicons';
import Post from '../../components/post';

const colors = {
    "gray": "#201f1d",
    "darkGray": "#161614",
    "lightGray": '#868686',
    "yellow": '#c4a000',
    "white": '#ffffff'
}

function TextOS(props) {
    const { texto, style } = props
    return(
        <Text style={{...style, color: '#ffffff', fontFamily: 'Kanit_400Regular'}}>{texto}</Text>
    )
}

const storeData = async (value) => {
  try {
    const jsonValue = JSON.stringify(value)
    await AsyncStorage.setItem('@uinfo', jsonValue)
  } catch (e) {
    console.log(e);
  }
}

const getData = async () => {
  try {
    const jsonValue = await AsyncStorage.getItem('@uinfo')
    return jsonValue != null ? JSON.parse(jsonValue) : null;
  } catch(e) {
    console.log(e);
  }
}

const clearData = async () => {
    try {
      let response = await AsyncStorage.removeItem('@uinfo')
      return response
    } catch(e) {
      console.log(e);
    }
  }

export default function HomeScreen({navigation}) {
    const [posts, setPosts] = React.useState([{}])

    React.useEffect(() => {
        fetch('http://localhost:3000/offside/posts', {method: 'GET'})
        .then(response => response.json())
        .then(response => {
                response.forEach(r => {
                    setPosts([...posts, r])
                    console.log(posts)
                })
            })
            .catch(err => console.error(err))
    }, [])

    getData().then((info) => {
        if (info !== null) {
            const options = {
                method: 'GET',
                headers: {
                'Content-Type': 'application/json',
                Authorization: info.token
                }
            }
            fetch("http://localhost:3000/offside/validate/" + info.uid, options)
            .then(response => response.json())
            .then(response => {
                if(!response.validation){
                    clearData().then(() => {
                        navigation.reset({
                            index: 0,
                            routes: [{name: 'Login'}],
                          });
                    })
                }
            })
            .catch(err => console.log(err))
        } else {
            clearData().then(() => navigation.reset({
                index: 0,
                routes: [{name: 'Login'}],
              }))
        }
        
    })
    

    let [fontsLoaded] = useFonts({
        Kanit_400Regular,
        Kanit_200ExtraLight,
        Kanit_700Bold
      });
    
      if (!fontsLoaded) {
        return null;
      }    
    
    return (
      <View style={styles.container}>
        <View style={{width: '100%'}}>
            <TouchableOpacity style={{display: 'flex', flexDirection: 'row', alignItems: 'center', backgroundColor: colors.yellow, width: 120, justifyContent: 'center', padding: 5}}>
                <Ionicons name="funnel" size={28} color={colors.darkGray} />
                <Text style={{...styles.font, color: colors.darkGray, fontSize: 20, marginLeft: 5}}>Filtrar</Text>
            </TouchableOpacity>
            {   
                posts.map((p, index) => {
                    <Post info={p} key={index} />
                })
            }
        </View>
      </View>
    );
}

const styles = StyleSheet.create({
    'container': {
        flex: 1,
        alignItems: 'center',
        backgroundColor: colors.gray,
        padding: 25
    },
    'login': {
        display: 'flex',
        alignContent: 'center',
        backgroundColor: colors.darkGray,
        width: '100%',
        padding: 25
    },
    'input':{
        padding: 10,
        color: '#ffffff',
        width: '100%',
        borderColor: colors.white,
        borderWidth: 1,
        borderStyle: 'solid',
        marginBottom: 10,
        fontFamily: 'Kanit_400Regular'
    },
    'cta':{
        padding: 10,
        width: "100%",
        display: 'flex',
        justifyContent: 'center',
        alignContent: 'center',
        borderColor: colors.white,
        borderWidth: 1,
        marginTop: 25
    },
    'white':{
        color: '#ffffff'
    },
    'font': {
        fontFamily: 'Kanit_400Regular'
    }
})
