import { View, Text, StyleSheet } from "react-native-web"

const colors = {
    "gray": "#201f1d",
    "darkGray": "#161614",
    "lightGray": '#868686',
    "yellow": '#c4a000',
    "white": '#ffffff'
}

export default function Post(props){
    const {info} = props

    return(
        <View styles={{display: 'flex', backgroundColor: colors.darkGray}}>
            <Text>{info.nome_usuario}</Text>
            <Text>{info.titulo}</Text>
            <Text>{info.corpo}</Text>
        </View>
    )
}
