import * as React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useFonts, Kanit_200ExtraLight, Kanit_400Regular, Kanit_700Bold} from '@expo-google-fonts/kanit';
import { TextInput } from 'react-native-web';
import AsyncStorage from '@react-native-async-storage/async-storage';

const colors = {
    "gray": "#201f1d",
    "darkGray": "#161614",
    "lightGray": '#868686',
    "yellow": '#c4a000',
    "white": '#ffffff'
}

const getData = async () => {
  try {
    const jsonValue = await AsyncStorage.getItem('@uinfo')
    return jsonValue != null ? JSON.parse(jsonValue) : null;
  } catch(e) {
    console.log(e);
  }
}

export default function PostScreen({navigation, route}) {
    const {id} = route
    const [info, setInfo] = React.useState({})

    const [votedUp, setVotedUp] = React.useState(false)
    const [votedDown, setVotedDown] = React.useState(false)

    const [voteCount, setVoteCount] = React.useState(info.votos)

    const voteHandler = (val) => {
        switch (val){
            case 1:
                if (votedDown) {
                    getData().then((uinfo) => {
                        const options = {
                            method: 'PUT',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid},"tipo":true}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                                console.log(response);
                                if (response.affectedRows == 0) {
                                    console.log("Incorretos")
                                    
                                } else {
                                    setVoteCount(voteCount + 2)
                                }
                            })
                            .catch(err => console.error(err));
                    })
                    setVotedDown(!votedDown)
                } else if(votedUp){
                    getData().then((uinfo) => {
                        const options = {
                            method: 'DELETE',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid}}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                              console.log(response)
                              if (response.affectedRows == 0) {
                                console.log("Incorretos")
                              } else {
                                setVoteCount(voteCount - 1)
                              }
                            })
                            .catch(err => console.error(err));
                    })
                    
                } else {
                    getData().then((uinfo) => {
                        const options = {
                            method: 'POST',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid},"tipo":true}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                                console.log(response)
                                if (response.affectedRows == 0) {
                                  console.log("Incorretos")
                                } else {
                                    setVoteCount(voteCount + 1)
                                }
                            })
                            .catch(err => console.error(err));
                    })
                }
                setVotedUp(!votedUp)
                break;

            case -1:
                if (votedUp) {
                    getData().then((uinfo) => {
                        const options = {
                            method: 'PUT',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid},"tipo":false}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                                console.log(response);
                                if (response.affectedRows == 0) {
                                    console.log("Incorretos")
                                } else {
                                    setVoteCount(voteCount - 2)
                                }
                            })
                            .catch(err => console.error(err));
                    })
                    setVotedUp(!votedUp)
                } else if (votedDown){
                    getData().then((uinfo) => {
                        const options = {
                            method: 'DELETE',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid}}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                              console.log(response)
                              if (response.affectedRows == 0) {
                                console.log("Incorretos")
                              } else {
                                setVoteCount(voteCount + 1)
                              }
                            })
                            .catch(err => console.error(err));
                    })
                    
                } else {
                    getData().then((uinfo) => {
                        const options = {
                            method: 'POST',
                            headers: {'Content-Type': 'application/json', Authorization: uinfo.token},
                            body: `{"id_post":${info.id},"id_usuario":${uinfo.uid},"tipo":false}`
                          };
                          
                          fetch('http://localhost:3000/offside/votos', options)
                            .then(response => response.json())
                            .then(response => {
                                console.log(response)
                                if (response.affectedRows == 0) {
                                  console.log("Incorretos")
                                } else {
                                    setVoteCount(voteCount - 1)
                                }
                            })
                            .catch(err => console.error(err));
                    })
                }
                setVotedDown(!votedDown)
                break;

            default:
                break;
        }
    }

    React.useEffect(() => {
        if (info.voted !== undefined) {
            switch (info.voted) {
                case 1:
                    setVotedUp(true)
                    break;
                
                case 0:
                    setVotedDown(true)
                    break;
    
                default:
                    break;
            }
        }
    }, [])

    let [fontsLoaded] = useFonts({
        Kanit_400Regular,
        Kanit_200ExtraLight,
        Kanit_700Bold
      });
    
      if (!fontsLoaded) {
        return null;
      }

    React.useEffect(() => {
        const options = {method: 'GET'};

        fetch('http://localhost:3000/offside/post/' + id, options)
        .then(response => response.json())
        .then(response => {
            setInfo(response[0])
        })
        .catch(err => console.error(err));
    }, [])
    
    return (
      <View style={styles.container}>
        <TouchableOpacity style={{display: 'flex', backgroundColor: colors.darkGray, padding: 15, marginTop: 10, paddingLeft: 50, position: 'relative'}}>
            <View style={{position: 'absolute', left: 14, display: 'flex', alignItems: 'center'}}>
                <TouchableOpacity onPress={() => voteHandler(1)}>
                    <Image style={{width: 20, height: 20, rotate: '180deg'}} source={votedUp ? varrow : arrow}/>
                </TouchableOpacity>
                <TouchableOpacity onPress={() => voteHandler(-1)}>
                    <Image style={{width: 20, height: 20}} source={votedDown ? varrow : arrow}/>
                </TouchableOpacity>
                <Text style={{...styles.white, ...styles.font}}>{voteCount}</Text>
            </View>
            <Text style={{...styles.font, ...styles.white, fontSize: 17}}>{info.titulo}</Text>
            <Text style={{...styles.font, color: colors.lightGray, marginBottom: 8}}>{info.nome_usuario}</Text>
            <Text style={{...styles.font, ...styles.white, marginBottom: 8}}>{info.corpo}</Text>
            <View style={{display: 'flex', flexDirection: 'row', width: '100%'}}>
            {
                info.fotos.map((f, index) => {
                    if (f !== null) {
                        return(
                            <View key={index} style={{width: 80, height: 80, marginRight: 5}}>
                                <Image style={{flex:1, height:null, width:null, resizeMode: 'cover', opacity: 1}} source={{uri: montaImg(f)}} />
                            </View>
                        )
                    }
                })
            }
            </View>
            <View style={{display:"flex", flexDirection: "row", marginTop: 10, flexWrap: 'wrap'}}>
            {
                info.tags.map((t, index) => {
                    return(
                        <View style={{marginRight: 10, backgroundColor: getTagColor(t), paddingVertical: 5, paddingHorizontal: 10, borderRadius: 15}} key={index}>
                            <Text style={{...styles.white, ...styles.font}}>{t}</Text>
                        </View>
                    )
                })
            }
            </View>
        </TouchableOpacity>
      </View>
    );
}

const styles = StyleSheet.create({
    'container': {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: colors.gray,
        padding: 50
    },
    'login': {
        display: 'flex',
        alignContent: 'center',
        backgroundColor: colors.darkGray,
        width: '100%',
        padding: 25
    },
    'input':{
        padding: 10,
        color: '#ffffff',
        width: '100%',
        borderColor: colors.white,
        borderWidth: 1,
        borderStyle: 'solid',
        marginBottom: 10,
        fontFamily: 'Kanit_400Regular'
    },
    'cta':{
        padding: 10,
        width: "100%",
        display: 'flex',
        justifyContent: 'center',
        alignContent: 'center',
        borderColor: colors.white,
        borderWidth: 1,
        marginTop: 25
    },
    'white':{
        color: '#ffffff'
    },
    'font': {
        fontFamily: 'Kanit_400Regular'
    }
})
