import { View, Text, StyleSheet } from "react-native-web"

const colors = {
    "gray": "#201f1d",
    "darkGray": "#161614",
    "lightGray": '#868686',
    "yellow": '#c4a000',
    "white": '#ffffff'
}

export default function Post(props){
    const {info} = props

    return(
        <View style={{display: 'flex', backgroundColor: colors.darkGray}}>
            <Text style={{color: colors.lightGray}}>{info.nome_usuario}</Text>
            <Text style={{...styles.white}}>{info.titulo}</Text>
            <Text>{info.corpo}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    'white':{
        color: '#ffffff'
    },
    'font': {
        fontFamily: 'Kanit_400Regular'
    }
})
