import { View, Text, StyleSheet, Image } from "react-native-web"

const colors = {
    "gray": "#201f1d",
    "darkGray": "#161614",
    "lightGray": '#868686',
    "yellow": '#c4a000',
    "white": '#ffffff'
}

function montaImg(img) {
    if (img != null) {
        console.log(`data:image/png;base64,${img}`)
        return `data:image/png;base64,${img}`;
    } else
        return `./default.png`;
}

export default function Post(props){
    const {info} = props

    return(
        <View style={{display: 'flex', backgroundColor: colors.darkGray, padding: 15, marginTop: 10}}>
            <Text style={{...styles.font, color: colors.lightGray}}>{info.nome_usuario}</Text>
            <Text style={{...styles.font, ...styles.white, fontSize: 17, marginBottom: 8}}>{info.titulo}</Text>
            <Text style={{...styles.font, ...styles.white}}>{info.corpo}</Text>
            {
                info.fotos.map((f, index) => {
                    return(
                        <View key={index} style={{width: 80, height: 80}}>
                            <Image style={{flex:1, height:null, width:null, resizeMode: 'cover', borderRadius: '100%'}} source={{uri: montaImg(f)}} />
                        </View>
                    )
                })
            }
        </View>
    )
}

const styles = StyleSheet.create({
    'white':{
        color: '#ffffff'
    },
    'font': {
        fontFamily: 'Kanit_400Regular'
    }
})
